import * as React from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import Typography from '@mui/material/Typography';
import { CardActionArea } from '@mui/material';

export default function ActionAreaCard() {
  return (
    <>
    <Card sx={{ maxWidth: 500, marginTop: '100px',marginLeft: '100px',background:'lavender'}}>
      <CardActionArea>
        <CardContent>

          <Typography variant="h6" color="text.primary">
          You are cordially invited to join us in celebrating the half-saree function of 
          our beloved child, Gyanada. Your presence would mean the world to us, 
          and we warmly welcome you to share in this special occasion. 
          We look forward to your blessings and the joy of your company. 
          It promises to be a memorable day filled with love, traditions, and cherished moments. We sincerely hope you can grace us with your presence on this significant day!

          </Typography>
          <br></br> <br></br>
          <Typography variant="h4" color= "text.primary">
          Details:
          DATE & DAY: 26th November,2023, Sunday.
          VENUE: Hotel Daspalla (The Forum)
          </Typography>
        </CardContent>
      </CardActionArea>
    </Card>
    </>
  );
}
